    $geo = Get-ApartmentListings_GeoCode -City $City -State_Code $State_Code
    $Uri = "https://www.apartmentfinder.com/api/url/"
    $Headers = [ordered]@{
        "method"="POST"
        "authority"="www.apartmentfinder.com"
        "scheme"="https"
        "path"="/api/url/"
        "pragma"="no-cache"
        "cache-control"="no-cache"
        "sec-ch-ua"="`"Google Chrome`";v=`"89`", `"Chromium`";v=`"89`", `";Not A Brand`";v=`"99`""
        "accept"="application/json, text/javascript, */*; q=0.01"
        "dnt"="1"
        "x-requested-with"="XMLHttpRequest"
        "sec-ch-ua-mobile"="?0"
        "user-agent"="Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/89.0.4389.114 Safari/537.36"
        "origin"="https://www.apartmentfinder.com"
        "sec-fetch-site"="same-origin"
        "sec-fetch-mode"="cors"
        "sec-fetch-dest"="empty"
        "referer"="https://www.apartmentfinder.com/"
        "accept-encoding"="gzip, deflate"
        "accept-language"="en-US,en;q=0.9"
    }
    $ContentType = "application/json"
    $Body = "{`"Geography`":{`"ID`":`"bq5st89`",`"Display`":`"$($City), $($State_Code)`",`"GeographyType`":2,`"Address`":{`"City`":`"$($City)`",`"State`":`"$($State_Code)`",`"MarketName`":`"$($City)`",`"DMA`":`"$($City), $($State_Code)`"},`"Location`":{`"Latitude`":$($geo.Latitude),`"Longitude`":$($geo.Longitude)},`"BoundingBox`":{`"LowerRight`":{`"Latitude`":$($geo.South),`"Longitude`":$($geo.East)},`"UpperLeft`":{`"Latitude`":$($geo.North),`"Longitude`":$($geo.West)}}},`"Listing`":{`"MinBeds`":`"1`",`"MinRentAmount`":null,`"MaxRentAmount`":null}}"
    $r = [ApartmentFinder.Execute.HttpRequest]::Send(
        $uri,
        [system.net.Http.HttpMethod]::Post,
        $Headers,
        $null,
        $ContentType,
        $Body
    )
    $uri =  "$($r.ResponseText | ConvertFrom-Json |% url)" + "/q/?bt=1&am=65558"
    $pages = [ApartmentFinder.Pages]::New()
    [ApartmentFinder.Paginate]::GetNextPageAndJson($uri,$pages)
    $json = $pages.JsonString | ConvertFrom-Json
    $results = [ApartmentFinder.Results]::New()
    $page = 1
    $tasks = @()
    for($i = 0; $i -lt $json.about.count; $i++){
        if($i -gt 0){
            write-progress -PercentComplete ($i/($json.about.Count - 1)*100) -Status "$([math]::Round(($i/($json.about.Count - 1)*100),2))%" -Activity "ApartmentFinder :: page $($page) :: $($i) of $($json.about.Count - 1)"
        }
        $listing = [ApartmentFinder.Listing]::new()
        $j = $json.about[$i] | ConvertTo-Json
        while($tasks.Where({!$_.IsCompleted}).Count -gt 3){}
        $tasks += $results.AddResult($listing,$j)
    }
    while($tasks.Where({!$_.IsCompleted}))
    {
        $c = $tasks.Where({$_.IsCompleted}).Count
        Write-Progress -PercentComplete ($c/$tasks.Count*100) -Activity "$([math]::Round(($c/$tasks.Count*100),2))%" -Status "$($c) of $($tasks.Count) tasks complete"
    }
    $page = 2
    while($pages.hasMorePages)
    {
        [ApartmentFinder.Paginate]::GetNextPageAndJson($pages.NextPage,$pages)
        $json = $pages.JsonString | ConvertFrom-Json
        $tasks = @()
        for($i = 0; $i -lt $json.about.count; $i++){
            if($i -gt 0){
                write-progress -PercentComplete ($i/($json.about.Count - 1)*100) -Status "$([math]::Round(($i/($json.about.Count - 1)*100),2))%" -Activity "ApartmentFinder :: page $($page) :: $($i) of $($json.about.Count - 1)"
            }
            $listing = [ApartmentFinder.Listing]::new()
            $j = $json.about[$i] | ConvertTo-Json
            while($tasks.Where({!$_.IsCompleted}).Count -gt 3){}
            $tasks += $results.AddResult($listing,$j)
        }
        while($tasks.Where({!$_.IsCompleted}))
        {
            $c = $tasks.Where({$_.IsCompleted}).Count
            Write-Progress -PercentComplete ($c/$tasks.Count*100) -Activity "$([math]::Round(($c/$tasks.Count*100),2))%" -Status "$($c) of $($tasks.Count) tasks complete"
        }
        $page++
    }
    $all = $tasks.Count
    while($tasks.Where({!$_.IsCompleted}))
    {
        $completed = $tasks.Where({$_.IsCompleted}).Count
        write-progress -PercentComplete ($completed/$all*100) -Activity "$([math]::Round(($completed/$all*100),2))%" -Status "$($completed) tasks completed of $($all)"
    }
    return $results.listingsCollection
}